import React, { useEffect, useState } from "react"
import PropTypes from "react"
import { useAuth0 } from "@auth0/auth0-react"

import AppContext from "."
import { getProducts, getProductsByCategory } from "../api/productAPI"
import { getCategories, getCategoryById } from "../api/categoryAPI"
import { getCart } from "../api/cartAPI"

const LockerContextProvider = ({ children }) => {

  const { user } = useAuth0()
  const email = user ? user.email : ""

  const [products, setProducts] = useState([])
  const [categories, setCategories] = useState([])
  const [category, setCategory] = useState([])
  const [shoppingcart, setShoppingcart] = useState([])

  const context = {
    products,
    categories,
    category,
    shoppingcart
  }

  useEffect(() => {
    getProducts()
      .then((response) => {
        setProducts(response)
      })

    getCategories()
      .then((response) => {
        setCategories(response)
      })

    getCart()
      .then((response) => {
        setShoppingcart(response.filter(item => item.user === email))
      })

    if (window.location.pathname.includes("categories/")) {
      const id = window.location.pathname.match(/\d+/)[0]
      getCategoryById(id)
        .then((response) => {
          setCategory(response)
        })
    }
  }, [email])

  return (
    <AppContext.Provider value={context} >
      {children}
    </AppContext.Provider>
  )
}

LockerContextProvider.propTypes = {
  children: PropTypes.Component
}

export default LockerContextProvider
