import React, { useContext } from 'react'
import { Link } from 'react-router-dom'
import { Container, Row, Col } from 'react-bootstrap'
import Loader from 'react-loader-spinner'

import AppContext from '../contexts'

function Categories() {

  const { categories } = useContext(AppContext)

  const mapFirstTwoCategories = () => {
    return (
      categories.slice(0, 2).map(
        category =>
          <Col as={Link} to={`/categories/${category.categoryId}`} className="smallspaceabove black thin nounderline categoryBox" key={category.categoryId}>
            <div md="auto" className="medlarge mr-auto spacebeneath">
              {category.categoryName}
            </div>
            <img src={category.imageUrl} alt="categoryImg" />
          </Col>
      )
    )
  }

  const mapLastTwoCategories = () => {
    return (
      categories.slice(2, 4).map(
        category =>
          <Col as={Link} to={`/categories/${category.categoryId}`} className="smallspaceabove black thin nounderline categoryBox" key={category.categoryId}>
            <div md="auto" className="medlarge mr-auto spacebeneath">
              {category.categoryName}
            </div>
            <img src={category.imageUrl} alt="categoryImg" />
          </Col>
      )
    )
  }

  return (
    <div className="mx-auto headerSpacing spacebeneath">
      <h2 className="large spaceabove spacebeneath">Categories</h2>
      {categories.length > 0 ?
        <>
          <Container className="catContainer">
            <Row>
              {mapFirstTwoCategories()}
            </Row>
            <Row>
              {mapLastTwoCategories()}
            </Row>
          </Container>
        </> :
        <>
          <div className="spaceabove spacebeneath" />
          <Loader type="TailSpin" color="black" height={40} width={40} />
        </>}
    </div>
  )
}

export default Categories
