export const addFavoriteProduct = (item, user, isAuthenticated, setFavorites, saveToLocalStorage, handleShowLoginError) => {
  const userFavorite = { ...item, ...user }
  const newFavoriteList = [...favorites, userFavorite]

  if (isAuthenticated) {
    setFavorites(newFavoriteList)
    saveToLocalStorage(newFavoriteList)
  }
  else {
    handleShowLoginError()
  }
}

export const removeFavoriteProduct = (item) => {
  const newFavoriteList = favorites.filter(
    (favorite) => favorite.productId !== item.productId
  )

  if (isAuthenticated) {
    setFavorites(newFavoriteList)
    saveToLocalStorage(newFavoriteList)
  }
  else {
    handleShowLoginError()
  }
}
