import React, { useState, useEffect } from 'react'
import { Container, Button, Col, Row, Modal } from 'react-bootstrap'

import { getCart, postProduct } from '../api/cartAPI';
import { getProducts } from '../api/productAPI';

function Search(props) {

  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [products, setProducts] = useState([])
  const [shoppingcart, setShoppingcart] = useState([])
  const [search, setSearch] = useState('')
  const [filteredProducts, setFilteredProducts] = useState([])

  useEffect(() => {
    getProducts()
      .then((response) => {
        setProducts(response)
      })

    getCart()
      .then((response) => {
        setShoppingcart(response)
      })

    const filtered = products.filter((product) =>
      ((product.productName).toLowerCase()).includes(search.toLowerCase()) ||
      ((product.category.categoryName).toLowerCase()).includes(search.toLowerCase()) ||
      ((product.brand).toLowerCase()).includes(search.toLowerCase())
    )

    setFilteredProducts(filtered);
  }, [products, search])

  const addProduct = (product) => {
    const cartItems = []
    shoppingcart.map(item => cartItems.push(item.product.productId))

    // If the item already exists in the cart, show modal alert
    if (cartItems.includes(product.productId)) {
      handleShow()
    }
    // Otherwise add a new item to the cart
    else {
      postProduct(product)
        .then(
          window.setTimeout(() => {
            props.history.push('/cart')
          }, 500
          ))
    }
  }

  return (
    <div className="mx-auto headerSpacing">
      <h2 className="large spaceabove smallspacebeneath">Product Search</h2>
      <Container className="spaceabove spacebeneath">
        <form id="searchForm">
          <div className="form-group">
            <input type="text" data-testid="txtSearch" name="queryValue" className="form-control medium thin" value={search} onChange={(e) => setSearch(e.target.value)} placeholder="Type to search..." />
          </div>
        </form>
      </Container>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <h4 className="thin my-auto">This product is already in your cart</h4>
        </Modal.Header>
      </Modal>
      <Container id="searchContainer">
        <Row>
          {
            filteredProducts.length < products.length ?
              filteredProducts.map(product =>
                <>
                  <Col className="smallspaceabove largespacebeneath" key={product.productId}>
                    <div className="img__wrap mx-auto">
                      <img src={product.imageUrl} className="productImg" alt='product' />
                      <div className="img__description_layer">
                        <h5 className="thin">{product.brand} / {product.size}</h5><br />
                      </div>
                    </div>
                    <div className="thin medlarge">{product.productName}</div>
                    <div className="thin medium">${product.unitPrice.toFixed(2)}</div>
                    <div md="auto">
                      <Button onClick={() => addProduct(product)} className="spaceabove addButton">Add To Cart</Button>
                    </div>
                  </Col>
                </>
              )
              :
              <></>
          }
        </Row>
      </Container>
    </div>
  )
}

export default Search
