import { delAllProducts } from '../functions/product'

export const getCartTotal = (shoppingcart) => {
  var total = 0
  shoppingcart.map(p => total = p.product.unitPrice + total)
  return total.toFixed(2)
}

const handleToken = (shoppingcart, user, handleShowSuccess) => {
  delAllProducts(shoppingcart, user)
  handleShowSuccess()
}
