import React, { useState, useEffect } from "react"
import { Container, Col, Row, Button } from 'react-bootstrap'
import { useAuth0 } from "@auth0/auth0-react"
import { IoIosHeart } from "react-icons/io";

import arrow from '../images/arrow.png'

function Account(props) {

  const { user, isAuthenticated } = useAuth0()
  const name = user ? user.nickname : ""

  const [favorites, setFavorites] = useState([])

  useEffect(() => {
    const productFavorites = JSON.parse(
      localStorage.getItem('favorites')
    )

    setFavorites(productFavorites.filter(item => item.nickname === name))
  }, [name])

  const saveToLocalStorage = (items) => {
    localStorage.setItem('favorites', JSON.stringify(items));
  };

  const removeFavoriteProduct = (item) => {
    const newFavoriteList = favorites.filter(
      (favorite) => favorite.productId !== item.productId
    );

    setFavorites(newFavoriteList);
    saveToLocalStorage(newFavoriteList);
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    })
  }

  const addProduct = (product) => {
    const cartItems = []
    shoppingcart.map(item => cartItems.push(item.product.productId))

    if (isAuthenticated) {
      // If the item already exists in the cart, show modal alert
      if (cartItems.includes(product.productId)) {
        // handleShowAlreadyAdded()
      }
      // Otherwise add a new item to the cart
      else {
        postProduct(user.email, product)
          .then(
            window.setTimeout(() => {
              props.history.push('/cart')
            }, 1000
            ))
      }
    }
    // else {
    //   handleShowLoginError()
    // }
  }

  if (isAuthenticated) {
    return (
      <Container className="homeCont headerSpacing smallspacebeneath">
        <h4 className="thin spaceabove">Welcome back, {user.name}!</h4>
        <img className="spaceabove profImg" src={user.picture} alt={user.name} />
        <h5 className="spaceabove thin">Your email is:</h5>
        <h5 className="thin italic">{user.email}</h5>
        <div>
          {favorites.length > 0 ?
            <>
              <h4 className="thin spaceabove spacebeneath">Favorites</h4>
              <Container className="favoritesContainer spacebeneath">
                {favorites.map(product =>
                  <Row className="smallspaceabove spacebeneath" key={product.productId}>
                    <Col>
                      <img src={product.imageUrl} className="productImg" alt='product' />
                    </Col>
                    <Col className="my-auto">
                      <div className="thin medlarge">{product.productName}</div>
                      <div className="thin medium">Brand: {product.brand}</div>
                      <div className="thin medium">Size: {product.size}</div>
                      <div className="thin medium">${product.unitPrice.toFixed(2)}</div>
                      <IoIosHeart
                        onClick={() => removeFavoriteProduct(product)}
                        style={{ color: '#ff78ac' }}
                        className="heart"
                      />
                      <div md="auto">
                        <Button onClick={() => addProduct(product)} className="spaceabove addButton">Add To Cart</Button>
                      </div>
                    </Col>
                  </Row>
                )}
              </Container>
              <Button className="smallspaceabove spacebeneath arrowButton" onClick={() => scrollToTop()}>
                <img src={arrow} className="mx-auto arrowIcon" alt="arrow up icon from https://www.iconfinder.com/" />
              </Button>
            </>
            : null
          }
        </div>
      </Container>
    )
  }
  else {
    return (
      <Container className="homeCont headerSpacing smallspacebeneath">
        <h4 className="thin spaceabove">Sorry, you need to be logged in to view this page</h4>
      </Container>
    )
  }
}

export default Account
