import React, { useEffect, useState } from "react"
// import { useAuth0 } from "@auth0/auth0-react"
import PropTypes from "react"

import AppContext from "."
import { getProducts } from "../api/productAPI"
import { getCategories, getCategoryById } from "../api/categoryAPI"
// import { getCart } from "../api/cartAPI"

const LockerContextProvider = ({ children }, props) => {

  // const { user, isAuthenticated } = useAuth0()
  // const email = user ? user.email : ""

  const [products, setProducts] = useState([])
  const [categories, setCategories] = useState([])
  const [category, setCategory] = useState([])

  // const [shoppingcart, setShoppingcart] = useState([])

  const context = {
    products,
    categories,
    category
  }

  useEffect(() => {
    getProducts()
      .then((response) => {
        setProducts(response)
      })

    getCategories()
      .then((response) => {
        setCategories(response)
      })

    getCategoryById(props.location.pathname.match(/\d+/)[0])
      .then((response) => {
        setCategory(response)
      })
  }, [id])


  return (
    <AppContext.Provider value={context} >
      {children}
    </AppContext.Provider>
  )
}

LockerContextProvider.propTypes = {
  children: PropTypes.Component
}

export default LockerContextProvider
