import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap'

const CustomForm = ({ status, message, onValidated }) => {

  const [email, setEmail] = useState('');

  const emailChangeHandler = (e) => {
    setEmail(e.target.value);
  }

  useEffect(() => {
    if (status === "success") clearFields();
  }, [status])

  const clearFields = () => {
    setEmail('');
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    email &&
      email.indexOf("@") > -1 &&
      onValidated({
        EMAIL: email
      });
  }

  return (
    <>
      <form className="spaceabove" onSubmit={(e) => handleSubmit(e)}>
        <h4 className="thin spacebeneath">
          {status === "success"
            ? "Success!"
            : "Sign up for our newsletter"
          }
        </h4>

        {status === "success" && (
          <div
            dangerouslySetInnerHTML={{ __html: message }}
          />
        )}

        {status !== "success" ? (
          <div>
            <input placeholder="Email" name="email" className="paddinghorizontal spacebeneath thin medium" value={email} onChange={emailChangeHandler} type="text"></input><br />
          </div>
        ) : null}

        {
          status === 'success' ?
            <button >Close</button>
            :
            // : <input
            //   label="subscribe"
            //   type="submit"
            //   formValues={[email]}
            // />
            <Button className="addButton spacebeneath" value="Subscribe" type="submit" >Subscribe</Button>

        }

        {/* <input placeholder="Email" name="email" className="paddinghorizontal spacebeneath thin medium" value={email} onChange={setEmail} type="text" isRequired></input><br />
        <Button className="addButton spacebeneath" value="Subscribe" type="submit" formValues={[email]}>Subscribe</Button> */}
      </form>
    </>
  );
}

export default CustomForm
