import React, { useState, useEffect } from "react"
import { Container, Col, Button } from 'react-bootstrap'
import { useAuth0 } from "@auth0/auth0-react"
import { IoIosHeart, IoIosHeartEmpty } from "react-icons/io";

function Account() {

  const { user, isAuthenticated } = useAuth0()

  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const productFavorites = JSON.parse(
      localStorage.getItem('favorites')
    )

    if (productFavorites) {
      setFavorites(productFavorites)
    }
  }, [])


  if (isAuthenticated) {
    return (
      <Container className="homeCont headerSpacing smallspacebeneath">
        <h4 className="thin spaceabove">Welcome back, {user.name}!</h4>
        <img className="spaceabove profImg" src={user.picture} alt={user.name} />
        <h5 className="spaceabove thin">Your email is:</h5>
        <h5 className="thin italic">{user.email}</h5>
        <div>
          {favorites.length > 0 ?
            <>
              {favorites.map(product => 
                <Col className="smallspaceabove largespacebeneath" key={product.productId}>
                  <div className="img__wrap mx-auto">
                    <img src={product.imageUrl} className="productImg" alt='product' />
                    <div className="img__description_layer">
                      <h5 className="thin">{product.brand} / {product.size}</h5><br />
                    </div>
                  </div>
                  <div className="thin medlarge">{product.productName}</div>
                  <div className="thin medium">${product.unitPrice.toFixed(2)}</div>
                  <div>
                    {favorites.includes(product) ?
                      <IoIosHeart
                        // onClick={() => removeFavoriteProduct(product)}
                        style={{ color: '#ff78ac' }}
                      />
                      :
                      <IoIosHeartEmpty
                        // onClick={() => addFavoriteProduct(product)}
                        style={{ color: '#ff78ac' }}
                      />
                    }
                  </div>
                  <div md="auto">
                    <Button className="spaceabove addButton">Add To Cart</Button>
                  </div>
                </Col>
              )}
            </>
            : null
          }
        </div>
      </Container>
    )
  }
  else {
    return (
      <Container className="homeCont headerSpacing smallspacebeneath">
        <h4 className="thin spaceabove">Sorry, you need to be logged in to view this page</h4>
      </Container>
    )
  }
}

export default Account
