import React, { useState, useEffect } from 'react';
import './mcFormStyles.scss';
import MailchimpSubscribe from "react-mailchimp-subscribe";
import InputField from "../../ui/InputField/InputField";

const CustomForm = ({ status, message, onValidated }) => {

  const [email, setEmail] = useState('');

  useEffect(() => {
    if(status === "success") clearFields();
  }, [status])

  const clearFields = () => {
    setFirstName('');
    setLastName('');
    setEmail('');
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    email &&
      email.indexOf("@") > -1 &&
      onValidated({
        EMAIL: email
      });
  }

  return (
    <>
      <form className="spaceabove" onSubmit={(e) => handleSubmit(e)}>
        <h4 className="thin spacebeneath">Sign up for our newsletter</h4>

        {status === "sending" && (
          <div>
            sending...
          </div>
        )}
        {status === "error" && (
          <div
            dangerouslySetInnerHTML={{ __html: message }}
          />
        )}
        {status === "success" && (
          <div
            dangerouslySetInnerHTML={{ __html: message }}
          />
        )}

        <input placeholder="Email" name="email" className="paddinghorizontal spacebeneath thin medium" value={email} onChange={setEmail} type="text" isRequired></input><br />
        <Button className="addButton spacebeneath" value="Subscribe" type="submit" formValues={[email]}>Subscribe</Button>
      </form>
    </>
  );
}

export default CustomForm
